/* Main container */
/* Main container */
/* Main container */

.wp-block-group.container {    
    margin: 0 auto;
    padding: 20px; 

}

/* Sections */
.wp-block-group.section {
    background-color: #ffffff;   
    padding: 30px;

}

/* Typography */
h2 {
    border-bottom: 2px solid #c62828;
    padding-bottom: 10px;
    margin-bottom: 20px;
}

h3 {
    font-size: 1.5em;   
    margin-top: 20px;
    margin-bottom: 10px;
}

p {
    font-size: 16px;
    line-height: 1.6;
    color: #333;
    margin-bottom: 15px;
}

/* Lists */
ul, ol {
    padding-left: 20px;
    margin-bottom: 15px;
}

ul li, ol li {
    margin-bottom: 5px;
}

ul li::before {    
    color: #4caf50;
    font-weight: bold;
    display: inline-block;
    width: 1em;
    margin-left: -1em;
}

/* Buttons */
.wp-block-button__link {
  background-color: rgb(220, 38, 38);
    color: white;
    padding: 15px 30px;
    text-decoration: none;
    border-radius: 5px;
    font-size: 1.2em;
    font-weight: bold;
    transition: background-color 0.3s;
}

.wp-block-button__link:hover {
    background-color: #45a049;
}

/* Responsive design */
@media (max-width: 768px) {
    .wp-block-group.container {
        padding: 10px;
    }

    .wp-block-group.section {
        padding: 20px;
    }

    h2 {
        font-size: 1.7em;
    }

    h3 {
        font-size: 1.3em;
    }
}

